    static public boolean isClassLoaded(String className) throws Exception {
        System.out.print("Is " + className + " loaded? ");

        java.lang.reflect.Method m = ClassLoader.class.getDeclaredMethod("findLoadedClass", new Class[] { String.class });
        m.setAccessible(true);
        ClassLoader cl = ConstantPoolMain.class.getClassLoader();
        Object loadedClass = m.invoke(cl, className);
        boolean b = (loadedClass != null);
        System.out.println(b + "");
        return b;
    }



    static public byte[] loadStringAsInputStream(String file) throws IOException {
        InputStream is = new FileInputStream(new File(file));
        return IOUtils.toByteArray(is);
    }


    static public class DynamicStringLoader {

        static public byte[] loadStringBytes(String className, String fieldName) throws Exception {
            // find class as though it was any other resource
            String resourceName = '/' + className.replace('.', '/') + ".class";
            URL url = DynamicStringLoader.class.getResource(resourceName);
            if (url == null) {
                throw new Exception("Unable to find class as resource [" + resourceName + "]");
            }

            // chop off path at end to get a URL we actually want to load from
            String resourcePath = url.toString();
            System.out.println("found resource @: " + resourcePath);
            int pos = resourcePath.lastIndexOf(resourceName);
            if (pos < 0) {
                throw new Exception("Unable to strip resource URL of final path");
            }

            // we always want to keep trailing '/'
            pos += 1;

            String resourceBasePath = resourcePath.substring(0, pos);
            System.out.println("resource base path: " + resourceBasePath);
            URL resourceBase = new URL(resourceBasePath);
            System.out.println("resource base: " + resourceBase);

            URLClassLoader classLoader = new URLClassLoader(new URL[] { resourceBase }, null);

            Class<?> type = classLoader.loadClass(className);

            System.out.println("Loaded class! " + type);
            System.out.println("  via cl: " + type.getClassLoader());

            if (fieldName == null) {
                return null;
            }

            Field field = type.getDeclaredField(fieldName);
            field.setAccessible(true);
            // field is static
            String s = (String)field.get(null);

            byte[] bytes = s.getBytes(StandardCharsets.UTF_8);

            System.out.println("-----------------------------");
            System.out.println("After temp classloader load");
            isClassLoaded("com.fizzed.rocker.ConstantPoolMain");
            isClassLoaded("com.fizzed.rocker.ConstantPoolMain$Strings");
            garbageCollectAndPrintMemory();

            // null field out
            field.set(null, null);

            return bytes;
        }

    }

    static public class MyClassLoader extends ClassLoader {

        public MyClassLoader() {
            // no parent classloader
            super(null);
        }

    }


